/*
* IMPORT MAIN REGISTER_MODULE ROUTES */
import modules_routes from "@/modules/routes";

export default [
  ...modules_routes,
  {
    path: '/',
    name: 'dashboard',
    meta: {
      authRequired: true,
    },
    component: () => import('./views/dashboards/dashboard'),
    children: [
